import React, { useState } from "react";
import "../styles/AiAssistant.css";

function AiAssistant() {
  const [assistantOpen, setAssistantOpen] = useState(false);
  const [userQuestion, setUserQuestion] = useState("");
  const [aiResponse, setAiResponse] = useState("");

  const toggleAssistant = () => {
    setAssistantOpen(!assistantOpen);
  };

  const handleSendQuestion = () => {
    // Por ahora mostramos una respuesta de ejemplo sin conexión a la API.
    setAiResponse("Esta es una respuesta de ejemplo.");
  };

  return (
    <div className="ai-assistant-container">
      <button onClick={toggleAssistant}>
        {assistantOpen ? "Close AI Assistant" : "Open AI Assistant"}
      </button>

      {assistantOpen && (
        <div className="assistant-panel">
          <h2>Ask our AI Assistant</h2>
          <textarea
            className="user-question"
            placeholder="Type your question here..."
            value={userQuestion}
            onChange={(e) => setUserQuestion(e.target.value)}
          />
          <button onClick={handleSendQuestion}>Send</button>
          <textarea
            className="ai-response"
            placeholder="AI response will appear here..."
            value={aiResponse}
            readOnly
          />
        </div>
      )}
    </div>
  );
}

export default AiAssistant;
